/*语言*/
var language = {
    "zh_cn": zh_cn,
    "en_us": en_us
};

/*****************调试使用**********************************************************/

/**
 * 根据id获取相应语言的文本
 * @param id
 * @return {string}
 */
tools.getString = function (id) {
    var lan = localStorage["LANGUAGE"];
    //如果localStorage中不存在，则使用中文
    if (!lan) {
        localStorage["LANGUAGE"] = 'zh_cn';//TODO 这里可以设置默认语言
        lan = 'zh_cn';
    }
    var str = language[lan];
    //根据语言获取语言对象
    if (str) {
        return str[id] ? str[id] : "";
    } else {
        return "";
    }
};

/**
 * 初始化网页中的文本
 */
tools.initLan = function () {
    var items = $("[data-lan]");
    for (var i = 0; i < items.length; i++) {
        var item = $(items[i]);
        var id = item.attr("data-lan");
        //如果是input标签，则修改placeholder中的文字
        if (item[0].tagName.toLocaleLowerCase() == 'input' || item[0].tagName.toLocaleLowerCase() == 'textarea') {
            item.attr('placeholder', tools.getString(id))
        } else {
            //原来是item.text(tools.getString(id))，会出现一些问题，所以这里使用.html()
            item.html(tools.getString(id));
        }
    }
};

/**
 * 设置当前语言
 * @param lan
 */
tools.setLan = function (lan) {
    localStorage["LANGUAGE"] = lan || "zh_cn";
};

/**
 * 得到当前语言
 * @return {string}
 */
tools.getLan = function () {
    var lan = '';
    try {
        lan = localStorage["LANGUAGE"];
    } catch (e) {
        alert(e);
    }
    return lan;
};

tools.initLan();
